import React, { useState, useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { Link } from 'react-router-dom';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

interface UrlMappingDTO {
  id: number;
  originalUrl: string;
  shortUrl: string;
  clickCount: number;
  createdDate: string;
  username: string;
}

interface ClickEventDTO {
  clickDate: string;
  count: number;
}

const Dashboard: React.FC = () => {
  const { user, logout } = useAuth();
  const [selectedUrl, setSelectedUrl] = useState<number | null>(null);
  const [startDate, setStartDate] = useState<string>(new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]);
  const [endDate, setEndDate] = useState<string>(new Date().toISOString().split('T')[0]);

  const { data: urls, isLoading: urlsLoading } = useQuery<UrlMappingDTO[]>({
    queryKey: ['urls'],
    queryFn: async () => {
      const response = await axios.get('http://localhost:8080/api/urls/myurls');
      console.log('URLs response:', response.data);
      return response.data;
    },
  });

  const { data: analytics, isLoading: analyticsLoading, error: analyticsError } = useQuery<ClickEventDTO[]>({
    queryKey: ['analytics', selectedUrl, startDate, endDate],
    queryFn: async () => {
      if (!selectedUrl) return [];
      console.log('Fetching analytics for URL:', selectedUrl);
      console.log('Date range:', { startDate, endDate });
      
      try {
        const response = await axios.get(`http://localhost:8080/api/urls/analytics/${selectedUrl}`, {
          params: {
            startDate: `${startDate}T00:00:00`,
            endDate: `${endDate}T23:59:59`
          }
        });
        
        console.log('Analytics response:', response.data);
        
        // Ensure we have an array of ClickEventDTO
        const data = Array.isArray(response.data) ? response.data : [];
        
        // Sort the data by date
        const sortedData = data.sort((a, b) => 
          new Date(a.clickDate).getTime() - new Date(b.clickDate).getTime()
        );
        
        console.log('Processed analytics data:', sortedData);
        return sortedData;
      } catch (error) {
        console.error('Error fetching analytics:', error);
        throw error;
      }
    },
    enabled: !!selectedUrl,
  });

  // Calculate total clicks from the filtered data
  const totalClicks = analytics ? analytics.reduce((sum, event) => sum + event.count, 0) : 0;

  // Prepare chart data
  const chartData = {
    labels: analytics?.map(event => new Date(event.clickDate).toLocaleDateString()) || [],
    datasets: [
      {
        label: 'Clicks',
        data: analytics?.map(event => event.count) || [],
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1,
        fill: false
      }
    ]
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top' as const,
      },
      title: {
        display: true,
        text: 'Clicks Over Time'
      },
      tooltip: {
        callbacks: {
          label: function(context: any) {
            return `Clicks: ${context.raw}`;
          }
        }
      }
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          stepSize: 1
        }
      },
      x: {
        ticks: {
          maxRotation: 45,
          minRotation: 45
        }
      }
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <nav className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex">
              <div className="flex-shrink-0 flex items-center">
                <Link to="/" className="text-xl font-bold text-blue-600">
                  URL Shortener
                </Link>
              </div>
            </div>
            <div className="flex items-center">
              <span className="text-gray-700 mr-4">Welcome, {user?.username}</span>
              <button
                onClick={logout}
                className="text-gray-700 hover:text-gray-900"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </nav>

      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* URL List */}
          <div className="lg:col-span-2">
            <h2 className="text-xl font-semibold mb-4">Your Shortened URLs</h2>
            {urlsLoading ? (
              <div className="animate-pulse">
                <div className="h-20 bg-gray-200 rounded mb-4"></div>
                <div className="h-20 bg-gray-200 rounded mb-4"></div>
                <div className="h-20 bg-gray-200 rounded"></div>
              </div>
            ) : (
              <div className="space-y-4">
                {urls?.map((url) => (
                  <div
                    key={url.id}
                    className={`p-4 border rounded-lg cursor-pointer transition-colors ${
                      selectedUrl === url.id ? 'border-blue-500 bg-blue-50' : 'border-gray-200'
                    }`}
                    onClick={() => setSelectedUrl(url.id)}
                  >
                    <div className="flex justify-between items-start">
                      <div>
                        <p className="text-sm text-gray-500">Original URL</p>
                        <p className="text-blue-600 hover:underline break-all">{url.originalUrl}</p>
                      </div>
                      <span className="text-sm text-gray-500">{url.clickCount} clicks</span>
                    </div>
                    <div className="mt-2">
                      <p className="text-sm text-gray-500">Short URL</p>
                      <a
                        href={url.shortUrl}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-blue-600 hover:underline break-all"
                      >
                        {url.shortUrl}
                      </a>
                    </div>
                    <div className="mt-2 text-sm text-gray-500">
                      Created: {new Date(url.createdDate).toLocaleDateString()}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Analytics */}
          <div>
            <h2 className="text-xl font-semibold mb-4">Analytics</h2>
            <div className="mb-4 space-y-2">
              <label className="block text-sm font-medium text-gray-700">Date Range</label>
              <div className="flex space-x-2">
                <input
                  type="date"
                  value={startDate}
                  onChange={(e) => setStartDate(e.target.value)}
                  className="px-3 py-2 border rounded-md"
                />
                <input
                  type="date"
                  value={endDate}
                  onChange={(e) => setEndDate(e.target.value)}
                  className="px-3 py-2 border rounded-md"
                />
              </div>
            </div>

            {!selectedUrl ? (
              <div className="text-gray-500 text-center py-8">
                Select a URL to view analytics
              </div>
            ) : analyticsLoading ? (
              <div className="animate-pulse">
                <div className="h-32 bg-gray-200 rounded mb-4"></div>
                <div className="h-32 bg-gray-200 rounded"></div>
              </div>
            ) : analyticsError ? (
              <div className="text-red-600 p-4 border border-red-200 rounded-lg">
                Error loading analytics: {analyticsError instanceof Error ? analyticsError.message : 'Unknown error'}
              </div>
            ) : (
              <div className="space-y-6">
                <div className="p-4 bg-white border rounded-lg">
                  <h3 className="text-lg font-medium mb-2">Total Clicks</h3>
                  <p className="text-3xl font-bold text-blue-600">{totalClicks}</p>
                  <p className="text-sm text-gray-500 mt-1">
                    Between {new Date(startDate).toLocaleDateString()} and {new Date(endDate).toLocaleDateString()}
                  </p>
                </div>

                <div className="p-4 bg-white border rounded-lg">
                  <h3 className="text-lg font-medium mb-2">Clicks Over Time</h3>
                  <div className="h-64">
                    <Line data={chartData} options={chartOptions} />
                  </div>
                </div>

                <div className="p-4 bg-white border rounded-lg">
                  <h3 className="text-lg font-medium mb-2">Daily Breakdown</h3>
                  <div className="space-y-2">
                    {analytics?.map((event) => (
                      <div key={event.clickDate} className="flex justify-between">
                        <span>{new Date(event.clickDate).toLocaleDateString()}</span>
                        <span className="font-medium">{event.count}</span>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
