import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';

interface UrlMappingDTO {
  id: number;
  originalUrl: string;
  shortUrl: string;
  clickCount: number;
  createdDate: string;
  username: string;
}

const Home: React.FC = () => {
  const { isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const [url, setUrl] = useState('');
  const [shortUrl, setShortUrl] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const response = await axios.post<UrlMappingDTO>('http://localhost:8080/api/urls/shorten', {
        originalUrl: url
      });

      // Ensure the short URL is properly formatted
      const formattedShortUrl = response.data.shortUrl.startsWith('http') 
        ? response.data.shortUrl 
        : `http://localhost:8080/${response.data.shortUrl}`;
      
      setShortUrl(formattedShortUrl);
      setUrl('');
    } catch (err) {
      setError('Failed to shorten URL. Please try again.');
      console.error('Error shortening URL:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <nav className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex">
              <div className="flex-shrink-0 flex items-center">
                <span className="text-xl font-bold text-blue-600">URL Shortener</span>
              </div>
            </div>
            <div className="flex items-center">
              {isAuthenticated ? (
                <button
                  onClick={() => navigate('/dashboard')}
                  className="text-blue-600 hover:text-blue-800"
                >
                  Dashboard
                </button>
              ) : (
                <div className="space-x-4">
                  <button
                    onClick={() => navigate('/login')}
                    className="text-blue-600 hover:text-blue-800"
                  >
                    Login
                  </button>
                  <button
                    onClick={() => navigate('/register')}
                    className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
                  >
                    Register
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      </nav>

      <div className="max-w-7xl mx-auto py-12 px-4 sm:px-6 lg:px-8">
        <div className="text-center">
          <h1 className="text-4xl font-bold text-gray-900 mb-8">
            Shorten Your URLs
          </h1>
          <div className="max-w-xl mx-auto">
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <input
                  type="url"
                  value={url}
                  onChange={(e) => setUrl(e.target.value)}
                  placeholder="Enter your URL here"
                  className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  required
                />
              </div>
              <button
                type="submit"
                disabled={loading}
                className="w-full bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 disabled:bg-blue-300"
              >
                {loading ? 'Shortening...' : 'Shorten URL'}
              </button>
            </form>

            {error && (
              <div className="mt-4 text-red-600">
                {error}
              </div>
            )}

            {shortUrl && (
              <div className="mt-8 p-4 bg-white border rounded-lg">
                <p className="text-gray-600 mb-2">Your shortened URL:</p>
                <a
                  href={shortUrl}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-600 hover:underline break-all"
                  onClick={async (e) => {
                    e.preventDefault();
                    try {
                      // First, try to redirect through our backend
                      const response = await axios.get(shortUrl, {
                        maxRedirects: 0,
                        validateStatus: (status) => status >= 200 && status < 400
                      });
                      
                      // If we get a redirect response, follow it
                      if (response.status === 302 || response.status === 301) {
                        window.location.href = response.headers.location;
                      } else {
                        // If no redirect, just open the URL
                        window.open(shortUrl, '_blank');
                      }
                    } catch (error) {
                      console.error('Error redirecting:', error);
                      // If there's an error, just open the URL directly
                      window.open(shortUrl, '_blank');
                    }
                  }}
                >
                  {shortUrl}
                </a>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
