import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';

interface UrlMappingDTO {
  id: number;
  originalUrl: string;
  shortUrl: string;
  clickCount: number;
  createdDate: string;
  username: string;
}

const Home: React.FC = () => {
  const [url, setUrl] = useState('');
  const [shortUrl, setShortUrl] = useState<UrlMappingDTO | null>(null);
  const [error, setError] = useState('');
  const { isAuthenticated } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setShortUrl(null);

    try {
      const response = await axios.post('http://localhost:8080/api/urls/shorten', {
        originalUrl: url,
      });
      setShortUrl(response.data);
    } catch (error) {
      setError('Failed to shorten URL. Please try again.');
      console.error('Error shortening URL:', error);
    }
  };

  return (
    <div className="max-w-2xl mx-auto">
      <h1 className="text-4xl font-bold text-center mb-8">Shorten Your URLs</h1>
      
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <input
            type="url"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            placeholder="Enter your long URL here"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>
        
        <button
          type="submit"
          className="w-full bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-600 transition-colors"
        >
          Shorten URL
        </button>
      </form>

      {error && (
        <div className="mt-4 p-4 bg-red-100 text-red-700 rounded-lg">
          {error}
        </div>
      )}

      {shortUrl && (
        <div className="mt-8 p-6 bg-white rounded-lg shadow-lg">
          <h2 className="text-xl font-semibold mb-4">Your Short URL</h2>
          <div className="space-y-4">
            <div>
              <p className="text-sm text-gray-500">Original URL</p>
              <p className="text-blue-600 hover:underline break-all">{shortUrl.originalUrl}</p>
            </div>
            <div>
              <p className="text-sm text-gray-500">Short URL</p>
              <a
                href={shortUrl.shortUrl}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-500 hover:underline break-all"
              >
                {shortUrl.shortUrl}
              </a>
            </div>
            <div className="text-sm text-gray-500">
              Created: {new Date(shortUrl.createdDate).toLocaleDateString()}
            </div>
          </div>
        </div>
      )}

      {!isAuthenticated && (
        <div className="mt-8 text-center">
          <p className="text-gray-600 mb-4">
            Want to track your URLs and view analytics?
          </p>
          <button
            onClick={() => navigate('/register')}
            className="bg-green-500 text-white py-2 px-6 rounded-lg hover:bg-green-600 transition-colors"
          >
            Create an Account
          </button>
        </div>
      )}
    </div>
  );
};

export default Home;
